import React, { Component } from 'react';
import ProjectsList from './projectsList';

class Featured extends Component {

	constructor(props){
	  super(props)
	  this.state = {
	  	river: "highlight",
	  	dd: "highlight",
	  	mm: "highlight"
	  }
	}	

	mouseEnter = (project) => {
		//return current project
		//this.props.hoverDisplay("river")
		this.setState({
	      river: "onlight"
	    })	
	}
	mouseExit = (project) => {
		//clear current
		this.setState({
	      river: "highlight"
	    })		
	}

	render() {
		return(
			<div className= "featured">
			  <ProjectsList toggleDisplay= {this.props.toggleDisplay} mouseExit= {this.mouseExit}  mouseEnter={this.mouseEnter}/>		 
			</div>
		)
	}
}

export default Featured
