import React, { Component } from 'react';

let spotlight = document.getElementsByClassName("spotText")

class Projects extends Component {
  constructor(props){
    super(props)
    this.state = {highlight: "highlight"}
  }

	mouseEnter = () => {
		//return current project
		this.setState({
	      highlight: "onlight"
	    })
	    spotlight.innerHTML = "hello"

	}
	mouseExit = () => {
		//clear current
		this.setState({
	      highlight: "highlight"
	    })		
	}   

  render() {
	return(
	  <div className= {this.state.highlight} onClick={this.props.toggleDisplay} onMouseEnter={this.mouseEnter} onMouseLeave={this.mouseExit}> 
		<h4>{this.props.project.name}</h4>
	  </div>
	)
  }
}

export default Projects
