import { render } from 'react-dom'
import React, { useRef } from 'react'
import clamp from 'lodash-es/clamp'
import { useSprings, animated } from 'react-spring'
import { useDrag } from 'react-use-gesture'
import '../test.css';

const pages = [
  'https://images.pexels.com/photos/62689/pexels-photo-62689.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=750&w=1260',
  'https://images.pexels.com/photos/351265/pexels-photo-351265.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=750&w=1260',
  'https://images.pexels.com/photos/924675/pexels-photo-924675.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=750&w=1260'
]

function Viewpager() {
  const index = useRef(0)
  let count = -1;
  let show;
  const [props, set] = useSprings(pages.length, i => ({
    //cursur location
    x: i * window.innerWidth,

    scale: 1,
    display: 'block'
  }))
  const bind = useDrag(({ down, movement: [mx], direction: [xDir], distance, cancel }) => {
    if (down && distance > window.innerWidth / 4)
      cancel((index.current = clamp(index.current + (xDir > 0 ? -1 : 1), 0, pages.length - 1)))
    set(i => {
      if (i < index.current - 1 || i > index.current + 1) return { display: 'none' }
      const x = (i - index.current) * window.innerWidth + (down ? mx : 0)
      const scale = down ? 1 - distance / window.innerWidth / 4 : 1
      return { x, scale, display: 'block' }
    })
  })
  const tryMe = (props) => {
    count++
    if (count > pages.length){
      count = -1;
    }
    show = <animated.div style={{ scale, backgroundImage: `url(${pages[i]})` }} />
    console.log(show)
  }


  return props.map(({ x, display, scale }, i) => (
    <animated.div {...bind()} key={i} style={{ display, x }}>
      <animated.div style={{ scale, backgroundImage: `url(${pages[i]})` }} />
      <div onClick={tryMe} className='tryMe'>tryMe</div>
    </animated.div>
  ))
}

export default Viewpager;
