import React, { Component } from 'react';
import {
  BrowserRouter as Router,
  Route
} from 'react-router-dom';
import Homepage from './containers/homepage';
import About from './containers/about';
import Background from './components/background';
import Navbar from './components/navbar';
import Footer from './components/footer';
import 'semantic-ui-css/semantic.min.css'

class App extends Component { 

  constructor(props){
    super(props)
    this.state = {
      currentPage: "/",
    }
  }

  pageSwitch(page) {
    localStorage.setItem( 'currentPage', page );
    this.setState({
      currentPage: page
    })
    console.log(page)
  }

  app = () => {
    console.log('state changed')
    console.log(this)
    this.setState({
      hover: true
    });
  }




  render(){
    return(
      <Router>
        <Route path="/" render={() => <div className="homeBase"><Background currentPage={localStorage.currentPage}/> <Footer/></div>} />
        <Route exact path="/" render={() =><div className="home"> <Navbar/> <Homepage pageSwitch={this.pageSwitch}/> </div> }/>
        <Route exact path="/about" render={() =><div className="home"> <Navbar/> <About pageSwitch={this.pageSwitch}/> </div> } />
      </Router>
    );
  }
}

export default App;
