import React, { Component } from 'react';

class Projects extends Component {

  constructor(props){
    super(props)
    this.state = {currentProject: ""}
  }

  componentDidMount() {
    console.log(this)
   }

	mouseEnter = () => {
		//return current project
		//this.props.hoverDisplay("river")
		this.setState({
	      highlight: "onlight"
	    })	
	}
	mouseExit = () => {
		//clear current
		this.setState({
	      highlight: "highlight"
	    })		
	}   

  render() {
	return(
	  <div className= {highlight} onClick={this.props.toggleDisplay} onMouseEnter={this.mouseEnter} onMouseLeave={this.mouseExit}> 
		<h4>{this.props.project.name}</h4>
	  </div>
	)
  }
}

export default Projects
