import React, { Component } from 'react';
import email from '../images/email.png';
import github from '../images/github.png';
import instagram from '../images/instagram.png';
import linkedin from '../images/linkedin.png';
import twitter from '../images/twitter.png';
import black from '../images/black.svg';
import { Image, Reveal } from 'semantic-ui-react'

class Navbar extends Component {

  handleOnClick = (icon) => {
    switch (icon) {
    	case 'instagram':
    		// statements_1
    		window.open('https://www.instagram.com/sixtyone19/')
    		break;
    	case 'github':
    		window.open('https://github.com/marsbhoward')
    		break;
   		case 'linkedin':
    		// statements_1
    		window.open('https://www.linkedin.com/in/mars-howard/')
    		break;
   		case 'twitter':
    		// statements_1
    		window.open('https://www.twitter.com/marsbhoward')
    		break;       		    		
    	default:
    		// email
    		this.copyText()
    		break;
    }
  }

//<link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/semantic-ui/2.4.0/semantic.min.css"/>
//semantic roll transition
//<div class="ui small circular rotate reveal image">
//  <img src="/images/wireframe/square-image.png" class="visible content">
//  <img src="/images/avatar/large/stevie.jpg" class="hidden content">
//</div>


  copyText = () => {
  	  let email = "marsbhoward@icloud.com"
	  var copyhelper = document.createElement("input");
	  copyhelper.className = 'copyhelper'
	  document.body.appendChild(copyhelper);
	  copyhelper.value = email;
	  copyhelper.select();
	  document.execCommand("copy");
	  document.body.removeChild(copyhelper);
  }

	render() {
	  return(
	    <div className ="navbar">
 	<Reveal animated='small fade'>
    	<Reveal.Content hidden>
      		<Image onClick={() => this.handleOnClick('email')} circular size='mini' src= {instagram} />
    	</Reveal.Content>
    	<Reveal.Content visible>
      		<Image circular size='mini' src= {black} />
    	</Reveal.Content>    	
  	</Reveal>	    

	  	  <img onClick={() => this.handleOnClick('github')} className= "inNavbar" src= {github}></img>
	  	  <img onClick={() => this.handleOnClick('instagram')} className= "inNavbar" src= {instagram}></img>
	  	  <img onClick={() => this.handleOnClick('linkedin')} className= "inNavbar" src= {linkedin}></img>
	  	  <img onClick={() => this.handleOnClick('twitter')} className= "inNavbar" src= {twitter}></img>
	    </div>
	  )
	}
}
export default Navbar
