import React, { Component } from 'react';

class Projects extends Component {
  constructor(props){
    super(props)
    this.state = {highlight: "highlight"}
  }

	mouseEnter = () => {
		//return current project
		this.setState({
	      highlight: "onlight"
	    })
	    //spotlight that describes project
	    this.props.description(
	    	<h4>{this.props.project.description}</h4>
	    )   
	    console.log(this)

	}
	mouseExit = () => {
		//clear current
		this.setState({
	      highlight: "highlight"
	    })
	    //clear spotlight text
	    this.props.description("hover over the featured projects to learn more about them. click on the project to get more indeph information.")		
	}   

  render() {
	return(
	  <div className= {this.state.highlight} onClick={this.props.toggleDisplay(this.props.project.video)} onMouseEnter={this.mouseEnter} onMouseLeave={this.mouseExit}> 
		<h4>{this.props.project.name}</h4>
	  </div>
	)
  }
}

export default Projects
